import React, { useState } from 'react';
import { Helmet } from 'react-helmet';
import Navbar8 from '../components/navbar8';
import Footer4 from '../components/footer4';
import './email-input.css';

const EmailInput = () => {
  const [formData, setFormData] = useState({
    senderEmail: '',
    subject: '',
    content: '',
    attachment: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({ ...prev, attachment: e.target.files[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formDataToSend = new FormData();
    formDataToSend.append('senderEmail', formData.senderEmail);
    formDataToSend.append('subject', formData.subject);
    formDataToSend.append('content', formData.content);
    if (formData.attachment) {
      formDataToSend.append('attachment', formData.attachment);
    }

    try {
      const response = await fetch('/api/analyze', {
        method: 'POST',
        body: formDataToSend,
      });

      const data = await response.json();
      console.log('Analysis result:', data);
    } catch (error) {
      console.error('Error analyzing email:', error);
    }
  };

  return (
    <div className="email-input-container">
      <Helmet>
        <title>Email Analysis | Superior Unique Pigeon</title>
      </Helmet>

      <Navbar8 />
      <main className="email-input-main">
        <h1>Email Analysis</h1>
        <p>Submit email details to check for phishing attempts</p>

        <form onSubmit={handleSubmit} className="email-input-form">
          {/* your form groups exactly as before */}
        </form>
      </main>
      <Footer4 />
    </div>
  );
};

export default EmailInput;
