import React, { useState } from 'react';
import './signup.css';

const Signup = ({ history }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
    setError('');
  };

  const isFakeEmail = (email) => {
    const fakePatterns = [
      /^test@/i,
      /^abc@/i,
      /^example@/i,
      /@example\.com$/i,
      /^user@/i,
      /^demo@/i,
      /^admin@/i
    ];
    return fakePatterns.some(pattern => pattern.test(email));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isFakeEmail(formData.email)) {
      setError('Please use a valid personal or work email address.');
      return;
    }

    console.log('User signed up with data:', formData);

    if (history) {
      history.push('/email-input');
    } else {
      window.location.href = '/email-input';
    }
  };

  return (
    <div className="signup-container">
      <main className="signup-main">
        <div className="signup-card">
          <h1>Sign Up</h1>
          <p>Create an account to track your scan history</p>
          {error && <p style={{ color: 'red' }}>{error}</p>}
          <form onSubmit={handleSubmit} className="signup-form">
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Username"
              required
            />
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email"
              required
            />
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Password"
              required
            />
            <button type="submit" className="signup-button">Sign Up</button>
          </form>
        </div>
      </main>
    </div>
  );
};

export default Signup;
