import React, { useState, useEffect } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import Navbar8 from '../components/navbar8';
import Footer4 from '../components/footer4';
import './email-input.css';

const EmailInput = () => {
  const history = useHistory();
  const location = useLocation();

  // Redirect to signup if not signed in
  useEffect(() => {
    const isSignedIn = localStorage.getItem('signedIn');
    if (!isSignedIn) {
      history.push('/signup');
    }
  }, []);

  const [formData, setFormData] = useState({
    senderEmail: '',
    subject: '',
    content: '',
    attachment: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({ ...prev, attachment: e.target.files[0] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted:', formData);
    // Here you would send the data to your backend
  };

  return (
    <div className="email-input-container">
      <Helmet>
        <title>Email Analysis | Superior Unique Pigeon</title>
      </Helmet>

      <Navbar8
        link1="Home"
        link2="Email Input"
        link3="History"
        action1="Sign In"
        action2="Verify Identity"
      />

      <main className="email-input-main">
        <h1>Email Analysis</h1>
        <p>Submit email details to check for phishing attempts</p>

        <form onSubmit={handleSubmit} className="email-input-form">
          <div className="form-group">
            <label>Sender Email</label>
            <input
              type="email"
              name="senderEmail"
              value={formData.senderEmail}
              onChange={handleChange}
              placeholder="sender@example.com"
              required
            />
          </div>

          <div className="form-group">
            <label>Subject</label>
            <input
              type="text"
              name="subject"
              value={formData.subject}
              onChange={handleChange}
              placeholder="Email subject"
              required
            />
          </div>

          <div className="form-group">
            <label>Email Content</label>
            <textarea
              name="content"
              value={formData.content}
              onChange={handleChange}
              placeholder="Paste the email content here..."
              rows="6"
              required
            />
          </div>

          <div className="form-group">
            <label>Attachment (Optional)</label>
            <input
              type="file"
              onChange={handleFileChange}
              className="file-input"
            />
          </div>

          <button type="submit" className="submit-button">
            Analyze Email
          </button>
        </form>
      </main>

      <Footer4 />
    </div>
  );
};

export default EmailInput;
