import React, { useState } from 'react';
import './signup.css'; // Ensure correct path

const Signup = ({ history }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Block fake or placeholder email domains
    const blockedDomains = ['example.com', 'mailinator.com', 'test.com', 'tempmail.com'];
    const emailDomain = formData.email.split('@')[1];

    if (blockedDomains.includes(emailDomain)) {
      alert('Please enter a legitimate email address.');
      return;
    }

    // Simulate signup logic
    console.log('User signed up with data:', formData);

    // Redirect to /email-input after signup
    if (history) {
      history.push('/email-input');
    } else {
      window.location.href = '/email-input'; // fallback
    }
  };

  return (
    <div className="signup-container">
      <main className="signup-main">
        <div className="signup-card">
          <h1>Sign Up</h1>
          <p>Create an account to track your scan history</p>
          <form onSubmit={handleSubmit} className="signup-form">
            <input
              type="text"
              name="username"
              value={formData.username}
              onChange={handleChange}
              placeholder="Username"
              required
            />
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Email"
              required
            />
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              placeholder="Password"
              required
            />
            <button type="submit" className="signup-button">Sign Up</button>
          </form>
        </div>
      </main>
    </div>
  );
};

export default Signup;
